mapper.select:
		select  from ms_secret_api
  		cli_id=#{cliId} and prj_id=#{prjId} and url=#{url}
  		limit 1

		select  from ms_secret_api

			prj_id=#{prjId}
			and url like concat(concat('%', #{url}), '%')

  		limit #{currentIndex}, #{size}

select count(*) from ms_secret_api

	prj_id=#{prjId}
	and url like concat(concat('%', #{url}), '%')
